  return async (dispatch, getState) => {
    try {
      let res = await editUserService(data);
      if (res && res.errCode === 0) {
        toast.success("update the user succeed");
        dispatch(editUserSuccess());
        dispatch(fetchAllUsersStart());
      } else {
